        public static readonly short[] sm4095 = new short[4095];  //s-matrix elements

        static Hadamard()
        {
            for (int i = 0; i < hdm4095.Length; i++) sm4095[i] = (short)(hdm4095[i] * 2 - 1);
        }

        public static int s4096(int i, int j)
        {
            //Values of 1 are added to the first row and columd to make an orthogonal matrix
            if (i < 1) return 1;
            if (j < 1) return 1;
            return sm4095[(4095 + i - j) % 4095];
        }

        public static void HTransformDriftCurve(double[] c, double[] cHt)
        {
            int nj = c.Length;
            double s;
            for (int j = 0; j < nj; j++)
            {
                s = 0;
                for (int i = 0; i < nj; i++) s += c[i] * Hadamard.s4096(i + 1, j + 1);
                cHt[j] = s / (nj / 2);
            }
            // recoved the average level
            s = c.Sum() * 4 / cHt.Length / cHt.Length;
            //s = 0.0;
            for (int i = 0; i < cHt.Length; i++)
            {
                cHt[i] += s;
            }
        }
    }
